import tensorflow as tfprint(tf.__version__)one = tf.constant(1)zero = tf.Variable(0)new_value = tf.add(zero, one)update = tf.assign(zero, new_value)init_op = tf.global_variables_initializer()session = tf.Session()print(session.run(init_op))print('hello')print(session.run(update))for _ in range(5):    session.run(update)    print(session.run(zero))#use of a placeholdera = tf.placeholder(tf.float32)b = a*2print(session.run(b,feed_dict={a:4}))#orprint(session.run(b,{a:4}))#feeding a vector of rank 1print(session.run(b,{a: [[2.,3.,5.],[1.,5.,10.]]}))#session.close()#Another way to use sessionwith tf.Session() as session:    result = session.run(b,{a:5})    print(result)#session.run(one)# Can't use this as session is already closed as with block finished.